/* Check device type
   -------------------------------------------------------------------------- */
    var isHandheld = null;
    var isTablet = null;
    var isSmartphone = null;
    var isDesktop = null;
    
    function checkDeviceType () {
        if (windowWidth >= 900) {
            isHandheld = isSmartphone = isTablet = false;
            isDesktop = true;
        } else if (windowWidth >= 800) {
            isHandheld = isTablet = true;
            isDesktop = isSmartphone = false;
        } else {
            isHandheld = isSmartphone = true;
            isDesktop = isTablet = false;
        }
    }
    
    checkDeviceType();


/* Scroll dispatcher
   -----------------------------------------------------*/
    $(window).scroll(function() {
        scaleBanner();
    });


/* Scale .banner upon scrolling
   -------------------------------------------------------------------------- */
    function scaleBanner() {
        // Selectors
        var container = $('.banner');
        if (container.length == 0)
            return;

            // Calculate scale
            var scrollTop = $(window).scrollTop();
            var scale = scrollTop/windowHeight;
            if (scale > 1) 
                scale = 1;

                if (isDesktop | isTablet) {
                    container.find('.banner-container').css({transform: 'scale('+(1-scale/3.5)+')'});
                    container.find('.banner-text').css({'margin-top': -150+scale*400});
                } else {
                    container.find('.banner-container').css({transform: ''});
                }

                // if (windowWidth <= 600) {
                //     aboutPosition = $('#about').offset().top;
                //     if (scrollTop > aboutPosition) {
                //         console.log(scrollTop);
                //         $('#nav-container').css({'position': 'relative'});
                //     } else {
                //         $('#nav-container').css({'position': 'fixed'});
                //     }
                // }

                aboutPosition = $('#about').offset().top;
                if (windowWidth >= 480) {
                    if (scrollTop > aboutPosition) {
                        $('#nav-container').css({'position': 'fixed'});
                    } else {
                        $('#nav-container').css({'position': 'relative'});

                    }
                }

                if ($('.project-page')[0]) {
                    container.find('.banner-background').css({opacity: scale/2});
                } else {
                    container.find('.banner-background').css({opacity: 0.8+scale/2});
                }
    };


/* Make logo scroll to top of page
   -----------------------------------------------------*/
    $('#nav-logo').click(function(){
        $("body").stop(true,true).animate({ scrollTop: 0 }, '1200', 'swing');
    });


/* Link to contact footer
   -----------------------------------------------------*/
    $('#nav-contact').click(function(){
        contactPosition = $('#contact').offset().top;
        $("html, body").stop(true, true).animate({ scrollTop: contactPosition }, '1200', 'swing');
    });

/* Design sprint links
   -----------------------------------------------------*/
    $('.design-sprint ul li a').click(function(){
        var href = $(this).attr('href');
        var sprintPosition = $(href).offset().top - $('#nav-container').height();
        $("html, body").stop(true, true).animate({ scrollTop: sprintPosition }, '1200', 'swing');
    });


